<script>
import { gsap } from 'gsap/all';

export default {
    data: () => ({
        shape: null,
        factorShape: 1,
        planet: null,
        factorPlanet: 2
    }),
    mounted() {
        this.initRefs();
    },
    methods: {
        initRefs() {
            this.shape = this.$refs.shape;
            this.planet = this.$refs.planet;
        },
        launchFloat() {
            gsap.to(this.shape, {
                duration: 3 * (this.factorShape * 0.1 + 1),
                repeat: -1,
                yoyo: true,
                y: -5 * this.factorShape - 5,
                ease: 'power1.inOut'
            });
            gsap.to(this.shape, {
                duration: 3 * (this.factorShape * 0.6 + 1),
                repeat: -1,
                yoyo: true,
                rotation: -5 * (this.factorShape * 0.5),
                ease: 'power1.inOut',
                transformOrigin: '50% 50%'
            });
            gsap.to(this.planet, {
                duration: 3 * (this.factorPlanet * 0.1 + 1),
                repeat: -1,
                yoyo: true,
                y: -5 * this.factorPlanet - 5,
                ease: 'power1.inOut',
                delay: 1.5
            });
            gsap.to(this.planet, {
                duration: 3 * (this.factorPlanet * 0.6 + 1),
                repeat: -1,
                yoyo: true,
                rotation: -5 * (this.factorPlanet * 0.5),
                ease: 'power1.inOut',
                transformOrigin: '50% 50%',
                delay: 1.5
            });
        },
        killFloat() {
            gsap.killTweensOf([this.shape, this.planet]);
        }
    }
};
</script>

