<script>
import { gsap } from 'gsap/all';

export default {
    data: () => ({
        randomized: null
    }),
    computed: {
        ready() {
            return this.$store.state.ready;
        },
        isMobile() {
            if (!this.$store.state.superWindow) return true;
            return this.$store.state.superWindow.width < this.$breakpoints.list.l;
        }
    },
    watch: {
        async ready(r) {
            if (!r) return;
            this.reveal();
        },
        isMobile(im) {
            if (!im) {
                this.randomized = [...this.$refs.illu.querySelectorAll('.front')].sort(() => Math.random() - 0.5);
                this.launchFloat();
            }
        }
    },
    mounted() {
        this.randomized = [...this.$refs.illu.querySelectorAll('.front')].sort(() => Math.random() - 0.5);
        if (this.ready) {
            this.launchFloat();
        }
    },
    methods: {
        reveal() {
            gsap.from(this.randomized, {
                duration: 2,
                opacity: 0,
                delay: 2.3,
                stagger: 0.1,
                y: -40,
                rotation: -5,
                ease: 'power2.out',
                transformOrigin: '50% 50%',
                onComplete: () => {
                    this.launchFloat();
                }
            });
            gsap.from(this.$refs.illu.querySelectorAll('.back'), {
                duration: 1,
                opacity: 0,
                delay: 1.5
            });
        },
        launchFloat() {
            this.randomized.forEach((el, i) => {
                const factor = el.getAttribute('data-factor');
                // Vertical translation
                gsap.to(el, {
                    duration: 3 * (factor * 0.1 + 1),
                    repeat: -1,
                    yoyo: true,
                    y: -5 * factor - 5,
                    ease: 'power1.inOut',
                    delay: (i - 1) * 0.5
                });

                // Rotation qui boucle plus lentement
                gsap.to(el, {
                    duration: 3 * (factor * 0.6 + 1),
                    repeat: -1,
                    yoyo: true,
                    rotation: -5 * (factor * 0.5),
                    ease: 'power1.inOut',
                    transformOrigin: '50% 50%',
                    delay: (i - 1) * 0.5
                });
            });
        }
    }
};
</script>

