<script>
import { gsap } from 'gsap/all';
import { query } from '@stereorepo/sac';

export default {
    data: () => ({
        planetsWatcher: null,
        planets: null,
        pillsWatcher: null,
        pills: null,
        pillsBigWatcher: null,
        pillsBig: null,
        cosmonautWatcher: null,
        cosmonaut: null,
        wrapperWatcher: null,
        wrapperIllus: null,
        back: null,
        front: null
    }),
    mounted() {
        this.$stereorepo.superScroll.initializeScroll();

        this.initRefs();

        gsap.set(this.back, { opacity: 0 });
        gsap.set(this.front, { opacity: 0, rotation: -5, y: -40 });

        this.wrapperWatcher = this.$stereorepo.superScroll
            .watch({
                element: this.$refs.wrapperIllus,
                options: {
                    stalk: false,
                    triggerOffset: '10%'
                }
            })
            .on('enter-view', () => {
                gsap.to(this.back, {
                    duration: 1,
                    opacity: 1,
                    ease: 'power1.inOut'
                });
                gsap.to(this.front, {
                    duration: 2,
                    opacity: 1,
                    stagger: 0.1,
                    delay: 0.6,
                    y: 0,
                    rotation: 0,
                    ease: 'power2.out',
                    transformOrigin: '50% 50%'
                });
            });

        this.initParallax();
    },
    beforeDestroy() {
        this.$stereorepo.superScroll.forgetMultiple(this.wrapperWatcher);
        this.$stereorepo.superScroll.destroyScroll();
    },
    methods: {
        initParallax() {
            // Watch an element
            this.planetsWatcher = this.$stereorepo.superScroll.watch({
                element: this.planets,
                options: {
                    speed: 1
                }
            });
            this.pillsWatcher = this.$stereorepo.superScroll.watch({
                element: this.pills,
                options: {
                    speed: 2
                }
            });
            this.pillsBigWatcher = this.$stereorepo.superScroll.watch({
                element: this.pillsBig,
                options: {
                    speed: 1
                }
            });
            this.cosmonautWatcher = this.$stereorepo.superScroll.watch({
                element: this.cosmonaut,
                options: {
                    speed: 1.5
                }
            });
        },
        initRefs() {
            this.planets = this.$refs.planets;
            this.pills = this.$refs.pills;
            this.pillsBig = this.$refs.pillsBig;
            this.cosmonaut = this.$refs.cosmonaut;
            this.wrapperIllus = this.$refs.wrapperIllus;
            this.back = query({ selector: '.back', ctx: this.wrapperIllus });
            this.front = query({ selector: '.front', ctx: this.wrapperIllus });
        }
    }
};
</script>

